mapper.select:
SELECT
r.id,
r.station_id AS stationId,
s.`name` AS stationName,
r.check_unit AS checkUnit,
r.sampling_time AS samplingTime,
r.report_time AS reportTime,
r.report_file AS reportFile,
r.rejection AS rejection,
r.is_examine AS isExamine,
r.update_by  AS reportName
FROM operation_report r
LEFT JOIN station s ON s.id = r.station_id
LEFT JOIN station_device sd ON sd.station_id = s.id


        r.station_id = #{query.stationId}


        AND sd.id = #{query.stationDeviceId}


        AND r.report_time >= #{query.startTime}


        AND r.report_time < #{query.endTime}


        AND r.update_by like concat('%',#{query.operationName},'%')


    AND s.is_test = 0

        AND


                OR s.province_code = #{permission.provinceCode}


                OR s.province_code in

                    #{item}



                OR s.city_code = #{permission.cityCode}


                OR s.city_code in

                    #{item}



                OR s.district_code = #{permission.districtCode}


                OR s.district_code in

                    #{item}



                OR s.id = #{permission.stationId}


                OR s.id in

                    #{item}





ORDER BY r.create_time DESC
