mapper.select:
SELECT t.type,t.code,t.name,t.description,
    t.order_no orderNo
FROM sys_constant t
where t.type = #{type,jdbcType=VARCHAR}
and t.code = #{code, jdbcType=VARCHAR}

SELECT t.type,t.code,t.name,t.description, t.order_no orderNo FROM sys_constant t where t.type in
#{type}
ORDER BY t.type,t.order_no
